import { Component } from '@angular/core';

@Component({
  selector: 'app-libro',
  templateUrl: './libro.component.html',
  styleUrls: ['./libro.component.css']
})
export class LibroComponent {

}
<!DOCTYPE html>
<html>
<head>
    <title>Libros</title>
</head>
<body>
    <h1>Lista de Libros</h1>

    <!-- Formulario para ingresar libros -->
    <h2>Agregar Libro</h2>
    <form id="formulario" onsubmit="agregarLibro(event)">
        <label for="titulo">Título:</label>
        <input type="text" id="titulo" required><br><br>

        <label for="referencia">Referencia:</label>
        <input type="text" id="referencia" required><br><br>

        <label for="autor">Autor:</label>
        <input type="text" id="autor" required><br><br>

        <label for="precio">Precio:</label>
        <input type="number" id="precio" step="0.01" required><br><br>

        <label for="ubicacion">Ubicación:</label>
        <input type="text" id="ubicacion" required><br><br>

        <input type="submit" value="Agregar">
    </form>

    <!-- Lista de libros -->
    <h2>Libros</h2>
    <ul id="lista-libros"></ul>

    <script>
        // Función para agregar un libro al formulario
        function agregarLibro(event) {
            event.preventDefault(); // Evita que se envíe el formulario

            // Obtener los valores del formulario
            var titulo = document.getElementById('titulo').value;
            var referencia = document.getElementById('referencia').value;
            var autor = document.getElementById('autor').value;
            var precio = document.getElementById('precio').value;
            var ubicacion = document.getElementById('ubicacion').value;

            // Crear un objeto libro con los valores
            var libro = {
                titulo: titulo,
                referencia: referencia,
                autor: autor,
                precio: parseFloat(precio),
                ubicacion: ubicacion
            };

            // Agregar el libro a la lista
            var listaLibros = document.getElementById('lista-libros');
            var libroElement = document.createElement('li');
            libroElement.textContent = `${libro.titulo} - ${libro.autor} - Precio: ${libro.precio}`;
            listaLibros.appendChild(libroElement);

            // Limpiar el formulario
            document.getElementById('formulario').reset();
        }
    </script>
</body>
</html>
